import "./styles.css";
import { ListGroup, ListGroupItem } from 'reactstrap';
import 'bootstrap/dist/css/bootstrap.css';

function TodoList1() {  
  return ( 
    <ListGroup>
      <ListGroupItem tag="a" href="#" className="align-self-center py-2 w-50 bg-info ">
        Cook Breakfast
      </ListGroupItem>
      <ListGroupItem tag="a" href="#" className="align-self-center py-2 w-50 bg-info ">
        Walk Dog
      </ListGroupItem>
      <ListGroupItem tag="a" href="#" className="align-self-center py-2 w-50 bg-info ">
        Take Shower
      </ListGroupItem>
    </ListGroup>
  );
}

function TodoList2() {  
  return ( 
    <ListGroup>
      <ListGroupItem tag="a" href="#" className="align-self-center py-2 w-50 bg-info ">
        Get Lunch
      </ListGroupItem>
      <ListGroupItem tag="a" href="#" className="align-self-center py-2 w-50 bg-info ">
        Run a Mile 
      </ListGroupItem>
      <ListGroupItem tag="a" href="#" className="align-self-center py-2 w-50 bg-info ">
        Take Bath 
      </ListGroupItem>
    </ListGroup>
  );
}

export default function App() { 

  let d = new Date(); 

  let day = new Array(7); 
  day[0] = "Sunday";
  day[1] = "Monday";
  day[2] = "Tuesday";        
  day[3] = "Wednesday";
  day[4] = "Thursday";
  day[5] = "Friday";
  day[6] = "Saturday";


  let n = day[d.getDay()]; 

  let todo; 

  if (n === "Monday") { 
    todo = <TodoList1 />; 
  } else if (n === "Tuesday") { 
    todo = <TodoList2 />;
  }

 return ( 
      <div className="App">
        <h1>{n}'s Todo List</h1>
        {  }
        {todo}
      </div>
    )

}
