import React, { Component } from "react";
import { ListGroup, ListGroupItem } from "reactstrap";
import { TodoData } from "./TodoData";
import "./styles.css";
import "bootstrap/dist/css/bootstrap.css";

class App extends React.Component {
  state = {
    todos: TodoData, // Importing the TodoData
    currentDay: new Date().getDay(), // Assuming Sunday as 0, Monday as 1, ..., Saturday as 6
    newTodoText: "",
    newTodoNote: "",
  };

  componentDidMount() {
    this.updateTodoList();
  }

  updateTodoList = () => {
    const currentDay = new Date().getDay();
    if (this.state.currentDay !== currentDay) {
      this.setState({ currentDay });
    }
  };

  addTodo = () => {
    const { newTodoText, newTodoNote, currentDay } = this.state;
    this.setState((prevState) => ({
      newTodoText: "",
      newTodoNote: "",
      todos: {
        ...prevState.todos,
        [currentDay]: [
          ...prevState.todos[currentDay],
          { id: Math.random(), text: newTodoText, note: newTodoNote, done: false },
        ],
      },
    }));
  };

  toggleDone = (id) => {
    this.setState((prevState) => ({
      todos: {
        ...prevState.todos,
        [this.state.currentDay]: prevState.todos[this.state.currentDay].map((todo) =>
          todo.id === id ? { ...todo, done: !todo.done } : todo
        ),
      },
    }));
  };

  handleTextChange = (event) => {
    this.setState({ newTodoText: event.target.value });
  };

  handleNoteChange = (id) => {
    const todo = this.state.todos[this.state.currentDay].find((todo) => todo.id === id);
    if (todo) {
      this.setState({ newTodoNote: todo.note, notebox: true });
    }
  };

  dismissNote = () => {
    this.setState({ newTodoNote: "", notebox: false });
  };

  render() {
    const { todos, currentDay, newTodoText, newTodoNote } = this.state;
    const todoList = todos[currentDay] || [];

    return (
      <div style={{ width: "50%", marginLeft: "25%" }}>
        <ListGroup>
          {this.state.notebox && (
            <ListGroupItem id="notebox" className="border border-dark rounded m-5">
              <div id="note" className="mt-1 font-weight-bold text-dark">
                {newTodoNote}
              </div>
              <button
                id="btn"
                className="w-25 mt-2 align-self-center bg-warning border-0"
                onClick={this.dismissNote}
              >
                Done
              </button>
            </ListGroupItem>
          )}
          {todoList.map((todo) => (
            <ListGroupItem
              key={todo.id}
              tag="button"
              onClick={() => this.handleNoteChange(todo.id)}
              className={`align-self-center py-2 w-50 ${todo.done ? "text-decoration-line-through" : ""}`}
            >
              {todo.text}
              <button className="btn btn-sm float-right" onClick={() => this.toggleDone(todo.id)}>
                {todo.done ? <i className="fas fa-check text-success"></i> : <i className="fas facheck"></i>}
              </button>
            </ListGroupItem>
          ))}
          <input
            type="text"
            placeholder="New todo"
            value={newTodoText}
            onChange={this.handleTextChange}
            style={{ width: '100%' }}
          />
          <input
            type="text"
            placeholder="Note"
            value={newTodoNote}
            onChange={this.handleNoteChange}
            style={{ width: '100%' }}
          />
          <button onClick={this.addTodo}>Add</button>
        </ListGroup>
      </div>
    );
  }
}

export default App;
