import React, { Component } from "react";
import { ListGroup, ListGroupItem } from "reactstrap";
import { TodoData } from "./TodoData";
import './styles.css';
import "bootstrap/dist/css/bootstrap.css";

function Todos(props) {
  const handleToggle = (todo) => {
    const updatedTodoList = props.todolists.map((item) => {
      if (item.id === todo.id) {
        return { ...item, done: !item.done };
      }
      return item;
    });

    props.updateTodoList(updatedTodoList);
  };

  return (
    <ListGroup>
      {props.todolists.map((todolist) => (
        <ListGroupItem
          key={todolist.id}
          tag="button"
          onClick={() => props.handleNote(todolist.note)}
          className={`align-self-center py-2 w-50 ${
            todolist.done ? "text-decoration-line-through" : ""
          }`}
        >
          {todolist.text}
          <button
            className="btn btn-sm float-right"
            onClick={() => handleToggle(todolist)}
          >
            {todolist.done ? (
              <i className="fas fa-check text-success"></i>
            ) : (
              <i className="fas fa-check"></i>
            )}
          </button>
        </ListGroupItem>
      ))}
      <ListGroupItem id="notebox" className="border border-dark rounded m-5">
        <div id="note" className="mt-1 font-weight-bold text-dark"></div>
        <button
          id="btn"
          className="w-25 mt-2 align-self-center bg-warning border-0"
          onClick={props.dismissNote}
        >
          Done
        </button>
      </ListGroupItem>
    </ListGroup>
  );
}

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      TodoList1: TodoData.TodoList1,
      TodoList2: TodoData.TodoList2,
      TodoList3: TodoData.TodoList3,
      TodoList4: TodoData.TodoList4,
      TodoList5: TodoData.TodoList5,
      TodoList6: TodoData.TodoList6,
      TodoList7: TodoData.TodoList7,
      newTodo: "", // New todo input
      todos: [], // Array to store todos
    };
  }

  handleNote = (note) => {
    document.getElementById("notebox").style.visibility = "visible";
    document.getElementById("note").innerHTML = note;
  };

  dismissNote = () => {
    document.getElementById("notebox").style.visibility = "hidden";
    document.getElementById("note").innerHTML = "";
  };

  updateTodoList = (updatedList) => {
    this.setState({
      TodoList1: updatedList,
    });
  };

  handleInputChange = (event) => {
    this.setState({ newTodo: event.target.value });
  };

  addTodo = (event) => {
    event.preventDefault();
    const { newTodo, todos } = this.state;

    if (newTodo.trim() === "") {
      return;
    }

    const newTodoItem = {
      id: Date.now(),
      text: newTodo,
      done: false,
    };

    this.setState((prevState) => ({
      todos: [...prevState.todos, newTodoItem],
      newTodo: "",
    }));
  };

  render() {
    let d = new Date();
    let day = [
      "Sunday",
      "Monday",
      "Tuesday",
      "Wednesday",
      "Thursday",
      "Friday",      "Saturday",
      "Sunday",
    ];
    let n = day[d.getDay()];

    let todo;

    if (n === "Monday") {
      todo = (
        <Todos
          todolists={this.state.TodoList1}
          handleNote={this.handleNote}
          dismissNote={this.dismissNote}
          updateTodoList={this.updateTodoList}
        />
      );
    } else if (n === "Tuesday") {
      todo = (
        <Todos
          todolists={this.state.TodoList2}
          handleNote={this.handleNote}
          dismissNote={this.dismissNote}
          updateTodoList={this.updateTodoList}
        />
      );
    } else if (n === "Wednesday") {
      todo = (
        <Todos
          todolists={this.state.TodoList3}
          handleNote={this.handleNote}
          dismissNote={this.dismissNote}
          updateTodoList={this.updateTodoList}
        />
      );
    } else if (n === "Thursday") {
      todo = (
        <Todos
          todolists={this.state.TodoList4}
          handleNote={this.handleNote}
          dismissNote={this.dismissNote}
          updateTodoList={this.updateTodoList}
        />
      );
    } else if (n === "Friday") {
      todo = (
        <Todos
          todolists={this.state.TodoList5}
          handleNote={this.handleNote}
          dismissNote={this.dismissNote}
          updateTodoList={this.updateTodoList}
        />
      );
    } else if (n === "Saturday") {
      todo = (
        <Todos
          todolists={this.state.TodoList6}
          handleNote={this.handleNote}
          dismissNote={this.dismissNote}
          updateTodoList={this.updateTodoList}
        />
      );
    } else if (n === "Sunday") {
      todo = (
        <Todos
          todolists={this.state.TodoList7}
          handleNote={this.handleNote}
          dismissNote={this.dismissNote}
          updateTodoList={this.updateTodoList}
        />
      );
    }

    return (
      <div className="App">
        <h1>
          <span className="d-flex justify-content-center text-warning">
            {n}'s
          </span>
          Todo List
        </h1>
        {todo}
        <form onSubmit={this.addTodo}>
          <div className="form-group">
            <input
              type="text"
              className="form-control"
              placeholder="Enter a new todo..."
              value={this.state.newTodo}
              onChange={this.handleInputChange}
            />
          </div>
          <button type="submit" className="btn btn-primary">
            Add Todo
          </button>
        </form>
        <div>
          <h3>Todos:</h3>
          <ul>
            {this.state.todos.map((todo) => (
              <li key={todo.id}>{todo.text}</li>
            ))}
          </ul>
        </div>
      </div>
    );
  }
}

export default App;
