import React, { Component } from "react";
import { ListGroup, ListGroupItem } from "reactstrap";
import { TodoData } from "./TodoData";
import "./styles.css";
import "bootstrap/dist/css/bootstrap.css";

class App extends React.Component {
  constructor() {
    super();

    this.state = {
      todos: TodoData,
      currentDay: 'TodoList1', // This can be updated to reflect the current day
      newTodoText: '',
      newTodoNote: ''
    };
  }

  addTodo = () => {
    const newTodo = {
      id: Date.now(),
      text: this.state.newTodoText,
      done: false,
      note: this.state.newTodoNote
    };

    this.setState(prevState => ({
      todos: {
        ...prevState.todos,
        [this.state.currentDay]: [
          ...prevState.todos[this.state.currentDay],
          newTodo
        ]
      },
      newTodoText: '',
      newTodoNote: ''
    }));
  }

  toggleDone = (id) => {
    this.setState(prevState => ({
      todos: {
        ...prevState.todos,
        [this.state.currentDay]: prevState.todos[this.state.currentDay].map(todo =>
          todo.id === id ? { ...todo, done: !todo.done } : todo
        )
      }
    }));
  }

  handleTextChange = (event) => {
    this.setState({ newTodoText: event.target.value });
  }

  handleNoteChange = (event) => {
    this.setState({ newTodoNote: event.target.value });
  }

  render() {
    return (
      <div style={{ width: '50%', marginLeft: '25%' }}> {/* Set the form width to 100% */}
        <ListGroup>
          <ListGroupItem id="notebox" className="border border-dark rounded m-5">
            <div id="note" className="mt-1 font-weight-bold text-dark">
              {this.state.newTodoNote}
            </div>
            <button
              id="btn"
              className="w-25 mt-2 align-self-center bg-warning border-0"
              onClick={this.dismissNote}
            >
              Done
            </button>
          </ListGroupItem>
          {this.state.todos[this.state.currentDay].map(todo => (
            <ListGroupItem
              key={todo.id}
              tag="button"
              onClick={() => this.handleNoteChange({ target: { value: todo.note } })}  // Use handleNoteChange instead of props
              className={`align-self-center py-2 w-50 ${
                todo.done ? 'text-decoration-line-through' : ''
              }`}
            >
              {todo.text}
              <button
                className="btn btn-sm float-right"
                onClick={() => this.toggleDone(todo.id)}
              >
                {todo.done ? (
                  <i className="fas fa-check text-success"></i>
                ) : (
                  <i className="fas fa-check"></i>
                )}
              </button>
            </ListGroupItem>
          ))}
          <input
            type="text"
            placeholder="New todo"
            value={this.state.newTodoText}
            onChange={this.handleTextChange}
            style={{ width: '100%' }}  // Set the input field width to 100%
          />
          <input
            type="text"
            placeholder="Note"
            value={this.state.newTodoNote}
            onChange={this.handleNoteChange}
            style={{ width: '100%' }}  // Set the input field width to 100%
          />
          <button onClick={this.addTodo}>Add</button>
        </ListGroup>
      </div>
    );
  }
}

export default App;
